import React from "react";
import {
  Paper,
  Grid,
  TextField,
  MenuItem,
  Slider,
  Typography,
} from "@mui/material";

const regions = ["Miền Bắc", "Miền Trung", "Miền Nam"];

const years = [2020, 2021, 2022, 2023];

function FilterSection({ filters, onFilterChange }) {
  const handleChange = (field) => (event) => {
    onFilterChange({
      ...filters,
      [field]: event.target.value,
    });
  };

  const handleScoreChange = (field) => (event, newValue) => {
    onFilterChange({
      ...filters,
      [field]: newValue,
    });
  };

  return (
    <Paper sx={{ p: 3, mb: 4 }}>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6} md={3}>
          <TextField
            select
            fullWidth
            label="Khu vực"
            value={filters.region}
            onChange={handleChange("region")}
          >
            <MenuItem value="">Tất cả</MenuItem>
            {regions.map((region) => (
              <MenuItem key={region} value={region}>
                {region}
              </MenuItem>
            ))}
          </TextField>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <TextField
            fullWidth
            label="Tên trường"
            value={filters.university}
            onChange={handleChange("university")}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <TextField
            fullWidth
            label="Ngành học"
            value={filters.major}
            onChange={handleChange("major")}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <TextField
            select
            fullWidth
            label="Năm"
            value={filters.year}
            onChange={handleChange("year")}
          >
            {years.map((year) => (
              <MenuItem key={year} value={year}>
                {year}
              </MenuItem>
            ))}
          </TextField>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Typography gutterBottom>Điểm tối thiểu</Typography>
          <Slider
            value={filters.minScore}
            onChange={handleScoreChange("minScore")}
            min={0}
            max={30}
            valueLabelDisplay="auto"
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <Typography gutterBottom>Điểm tối đa</Typography>
          <Slider
            value={filters.maxScore}
            onChange={handleScoreChange("maxScore")}
            min={0}
            max={30}
            valueLabelDisplay="auto"
          />
        </Grid>
      </Grid>
    </Paper>
  );
}

export default FilterSection;
