import React from "react";
import { Container, Box, Typography } from "@mui/material";
import Header from "./components/Header";
import FilterSection from "./components/FilterSection";
import UniversityList from "./components/UniversityList";
import { universitiesData } from "./data/universitiesData";

function App() {
  const [filters, setFilters] = React.useState({
    region: "",
    university: "",
    major: "",
    year: 2024,
    minScore: 0,
    maxScore: 30,
  });
  const [page, setPage] = React.useState("home");

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  const handleChangePage = (newPage) => {
    setPage(newPage);
  };

  return (
    <Box sx={{ minHeight: "100vh" }}>
      <Header currentPage={page} onChangePage={handleChangePage} />
      <Container maxWidth="lg" sx={{ py: 2 }}>
        {page === "home" && (
          <>
            <Typography
              variant="h4"
              component="h1"
              gutterBottom
              align="center"
              sx={{ mb: 2 }}
            >
              Thống Kê Điểm Thi Đại Học
            </Typography>
            <FilterSection
              filters={filters}
              onFilterChange={handleFilterChange}
            />
            <UniversityList filters={filters} data={universitiesData} />
          </>
        )}
        {page !== "home" && (
          <Box sx={{ textAlign: "center", py: 8 }}>
            <Typography variant="h5">Tính năng đang phát triển...</Typography>
          </Box>
        )}
      </Container>
    </Box>
  );
}

export default App;
