import React from "react";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Box,
  Chip,
} from "@mui/material";

function UniversityList({ filters, data }) {
  const filteredData = data.filter((university) => {
    return (
      (!filters.region || university.region === filters.region) &&
      (!filters.university ||
        university.name
          .toLowerCase()
          .includes(filters.university.toLowerCase())) &&
      (!filters.major ||
        university.majors.some(
          (major) =>
            major.name.toLowerCase().includes(filters.major.toLowerCase()) &&
            major.scores[filters.year] >= filters.minScore &&
            major.scores[filters.year] <= filters.maxScore
        ))
    );
  });

  if (filteredData.length === 0) {
    return (
      <Box sx={{ textAlign: "center", py: 4 }}>
        <Typography variant="h6" color="text.secondary">
          Không tìm thấy kết quả phù hợp
        </Typography>
      </Box>
    );
  }

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Tên trường</TableCell>
            <TableCell>Khu vực</TableCell>
            <TableCell>Ngành học</TableCell>
            <TableCell>Điểm chuẩn ({filters.year})</TableCell>
            <TableCell>Chỉ tiêu</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredData.map((university) => (
            <React.Fragment key={university.id}>
              {university.majors
                .filter(
                  (major) =>
                    !filters.major ||
                    major.name
                      .toLowerCase()
                      .includes(filters.major.toLowerCase())
                )
                .map((major, index) => (
                  <TableRow key={`${university.id}-${major.id}`}>
                    {index === 0 && (
                      <TableCell rowSpan={university.majors.length}>
                        {university.name}
                      </TableCell>
                    )}
                    {index === 0 && (
                      <TableCell rowSpan={university.majors.length}>
                        <Chip
                          label={university.region}
                          color="primary"
                          variant="outlined"
                        />
                      </TableCell>
                    )}
                    <TableCell>{major.name}</TableCell>
                    <TableCell>{major.scores[filters.year]}</TableCell>
                    <TableCell>{major.quota}</TableCell>
                  </TableRow>
                ))}
            </React.Fragment>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default UniversityList;
