import React from "react";
import { AppBar, Toolbar, Typography, Box, Tabs, Tab } from "@mui/material";
import SchoolIcon from "@mui/icons-material/School";

const pages = [
  { label: "Trang chủ", value: "home" },
  { label: "Tin tức", value: "news" },
  { label: "Review trường", value: "review" },
  { label: "So sánh điểm", value: "compare" },
];

function Header({ currentPage = "home", onChangePage }) {
  const [tab, setTab] = React.useState(currentPage);
  const handleTabChange = (e, newValue) => {
    setTab(newValue);
    if (onChangePage) onChangePage(newValue);
  };
  return (
    <AppBar position="static" color="primary" elevation={1}>
      <Toolbar sx={{ minHeight: 56 }}>
        <SchoolIcon sx={{ mr: 1 }} />
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Thống Kê Điểm Thi Đại Học
        </Typography>
        <Tabs
          value={tab}
          onChange={handleTabChange}
          textColor="inherit"
          indicatorColor="secondary"
          sx={{ minHeight: 40 }}
        >
          {pages.map((page) => (
            <Tab
              key={page.value}
              label={page.label}
              value={page.value}
              sx={{ minHeight: 40 }}
            />
          ))}
        </Tabs>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
